import { Component, Input, OnChanges, OnInit, SimpleChanges, ViewChild, ElementRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import * as pdfjsLib from 'pdfjs-dist';

pdfjsLib.GlobalWorkerOptions.workerSrc = '/assets/pdf.worker.min.mjs';

@Component({
  selector: 'app-pdf-viewer',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      class="relative bg-gray-100 flex-1 overflow-hidden flex items-center justify-center"
      (mousedown)="onMouseDown($event)"
      (mousemove)="onMouseMove($event)"
      (mouseup)="onMouseUp()"
      (mouseleave)="onMouseUp()"
    >
      <div
        #wrapper
        class="transition-transform"
        [style.transform]="getTransform()"
        [style.transitionDuration]="isPanning ? '0ms' : '100ms'"
      >
        <canvas #pdfCanvas class="bg-white block" [style.cursor]="isPanning ? 'grabbing' : 'grab'"></canvas>
        <canvas
          #overlayCanvas
          class="absolute inset-0 pointer-events-auto"
          [style.cursor]="(mode === 'fiber' || mode === 'conduit' || mode === 'calibrate') ? 'crosshair' : 'auto'"
        ></canvas>
      </div>

      <div class="absolute top-4 left-4 right-4 flex flex-wrap items-start justify-between gap-3 pointer-events-auto">
        <div class="bg-white p-2 rounded shadow flex flex-col gap-2">
          <div class="flex gap-1">
            <button (click)="prevPage()" [disabled]="currentPage <= 1" class="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 disabled:opacity-50">&lt;</button>
            <span class="px-3 py-1 text-sm whitespace-nowrap">Page {{ currentPage }} / {{ pdf?.numPages || 1 }}</span>
            <button (click)="nextPage()" [disabled]="currentPage >= (pdf?.numPages || 1)" class="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 disabled:opacity-50">&gt;</button>
          </div>
          <div class="flex gap-1">
            <button (click)="zoomOut()" class="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300">−</button>
            <span class="px-3 py-1 text-sm w-16 text-center">{{ Math.round(zoom * 100) }}%</span>
            <button (click)="zoomIn()" class="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300">+</button>
          </div>
          <div class="text-xs text-gray-600 text-center">Drag to pan</div>
        </div>

        <div class="bg-white/95 backdrop-blur p-3 rounded shadow flex flex-col gap-2 w-full md:w-auto max-w-3xl">
          <div class="flex flex-wrap gap-2">
            <button (click)="setMode('pan')" [class.bg-blue-600]="mode==='pan'" [class.text-white]="mode==='pan'" class="px-3 py-1 rounded border">Pan</button>
            <button (click)="setMode('calibrate')" [class.bg-blue-600]="mode==='calibrate'" [class.text-white]="mode==='calibrate'" class="px-3 py-1 rounded border">Calibrate</button>
            <button (click)="setMode('fiber')" [class.bg-blue-600]="mode==='fiber'" [class.text-white]="mode==='fiber'" class="px-3 py-1 rounded border">Draw Fiber</button>
            <button (click)="setMode('conduit')" [class.bg-blue-600]="mode==='conduit'" [class.text-white]="mode==='conduit'" class="px-3 py-1 rounded border">Draw Conduit</button>
            <button (click)="setMode('terminal')" [class.bg-blue-600]="mode==='terminal'" [class.text-white]="mode==='terminal'" class="px-3 py-1 rounded border">Terminal</button>
            <button (click)="setMode('drop')" [class.bg-blue-600]="mode==='drop'" [class.text-white]="mode==='drop'" class="px-3 py-1 rounded border">Drop Ped</button>
            <button (click)="clearOverlay()" class="px-3 py-1 rounded border">Clear</button>
          </div>
          <div class="text-xs text-gray-700 flex flex-wrap gap-x-4 gap-y-1">
            <div *ngIf="!scaleFeetPerPixel">Calibrate: click two points on scale, enter real feet.</div>
            <div *ngIf="scaleFeetPerPixel">Scale: {{ (scaleFeetPerPixel*12).toFixed(4) }} inches/px ({{ (scaleFeetPerPixel).toFixed(4) }} ft/px)</div>
            <div>Fiber total: {{ totalLengthFeet.toFixed(1) }} ft</div>
            <div>Conduit total: {{ totalConduitFeet.toFixed(1) }} ft</div>
            <div>Markers: {{ terminals.length }} terminals, {{ drops.length }} drops</div>
          </div>
        </div>
      </div>

      <div *ngIf="toastMessage" class="absolute top-2 left-1/2 -translate-x-1/2 bg-rose-600 text-white px-4 py-2 rounded shadow text-sm pointer-events-none">
        {{ toastMessage }}
      </div>
    </div>
  `,
  styles: [`
    :host {
      display: flex;
      height: 100%;
      width: 100%;
    }
  `]
})
export class PDFViewerComponent implements OnInit, OnChanges {
  @ViewChild('pdfCanvas') canvasRef!: ElementRef<HTMLCanvasElement>;
  @ViewChild('wrapper') wrapperRef!: ElementRef<HTMLDivElement>;
  @ViewChild('overlayCanvas') overlayRef!: ElementRef<HTMLCanvasElement>;
  @Input() pdfUrl: string = '';

  pdf: any = null;
  currentPage = 1;
  zoom = 1;
  panOffset = { x: 0, y: 0 };
  isPanning = false;
  Math = Math;

  mode: 'pan' | 'calibrate' | 'fiber' | 'conduit' | 'terminal' | 'drop' = 'pan';
  calibrationPoints: { x: number; y: number }[] = [];
  scaleFeetPerPixel: number | null = null;
  drawingPath: { x: number; y: number }[] = [];
  hoverPoint: { x: number; y: number } | null = null;
  polylines: { points: { x: number; y: number }[]; lengthFt: number; type: 'fiber' | 'conduit' }[] = [];
  totalLengthFeet = 0;
  totalConduitFeet = 0;
  terminals: { x: number; y: number }[] = [];
  drops: { x: number; y: number }[] = [];
  selectedEndpoint: { point: { x: number; y: number }; type: 'terminal' | 'drop' } | null = null;
  toastMessage: string | null = null;
  private toastTimer: any = null;

  private renderTask: any = null;
  private lastMouse = { x: 0, y: 0 };
  private overlayCtx: CanvasRenderingContext2D | null = null;

  async ngOnInit() {
    if (this.pdfUrl) {
      await this.loadPdf();
    }
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes['pdfUrl'] && !changes['pdfUrl'].firstChange && this.pdfUrl) {
      this.loadPdf();
    }
  }

  async loadPdf() {
    if (!this.pdfUrl) return;
    try {
      const response = await fetch(this.pdfUrl);
      if (!response.ok) {
        console.error('Failed to fetch PDF:', response.status, response.statusText);
        return;
      }
      const blob = await response.blob();
      const buffer = await blob.arrayBuffer();
      const typedArray = new Uint8Array(buffer);
      this.pdf = await pdfjsLib.getDocument({ data: typedArray }).promise;
      this.currentPage = 1;
      this.panOffset = { x: 0, y: 0 };
      await this.renderPage();
    } catch (error) {
      console.error('Error loading PDF:', error);
    }
  }

  async renderPage() {
    if (!this.pdf || !this.canvasRef) return;

    if (this.renderTask) {
      try {
        this.renderTask.cancel();
      } catch (err) {}
    }

    try {
      const page = await this.pdf.getPage(this.currentPage);
      const viewport = page.getViewport({ scale: this.zoom });

      const canvas = this.canvasRef.nativeElement;
      const context = canvas.getContext('2d');
      if (!context) return;

      canvas.width = viewport.width;
      canvas.height = viewport.height;

      // keep overlay in sync
      if (this.overlayRef) {
        const overlay = this.overlayRef.nativeElement;
        overlay.width = viewport.width;
        overlay.height = viewport.height;
        this.overlayCtx = overlay.getContext('2d');
        this.redrawOverlay();
      }

      const renderContext = { canvasContext: context, viewport };
      this.renderTask = page.render(renderContext);
      await this.renderTask.promise;
    } catch (error) {
      console.error('Render error:', error);
    }
  }

  onMouseDown(event: MouseEvent) {
    if (this.mode === 'pan') {
      this.isPanning = true;
      this.lastMouse = { x: event.clientX, y: event.clientY };
      return;
    }

    if (!this.overlayRef) return;
    const rect = this.overlayRef.nativeElement.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    const pdfPoint = { x: x / this.zoom, y: y / this.zoom };

    if (this.mode === 'terminal') {
      this.terminals.push(pdfPoint);
      this.redrawOverlay();
      return;
    }

    if (this.mode === 'drop') {
      this.drops.push(pdfPoint);
      this.redrawOverlay();
      return;
    }

    if (this.mode === 'calibrate') {
      this.calibrationPoints.push(pdfPoint);
      if (this.calibrationPoints.length === 2) {
        this.finishCalibration();
      }
      this.redrawOverlay();
      return;
    }

    if (this.mode === 'fiber') {
      this.isPanning = false;
      this.hoverPoint = null;
      if (this.drawingPath.length === 0) {
        this.drawingPath = [pdfPoint];
      } else {
        this.drawingPath = [...this.drawingPath, pdfPoint];
      }

      if (event.detail >= 2) {
        this.commitDrawingPath();
      } else {
        this.redrawOverlay();
      }
      return;
    }

    if (this.mode === 'conduit') {
      this.isPanning = false;
      const start = this.findNearestEndpoint(pdfPoint);
      if (!this.selectedEndpoint) {
        if (start) {
          this.selectedEndpoint = start;
          this.redrawOverlay();
        }
        return;
      }

      // second click must land on a drop pedestal
      const end = this.findNearestEndpoint(pdfPoint, 'drop');
      if (!end) {
        this.showToast('Select a drop pedestal to complete the conduit');
        return;
      }

      // enforce terminal->drop or drop->drop; block terminal->terminal
      if (this.selectedEndpoint.type === 'terminal' && end.type === 'terminal') {
        this.showToast('Cannot connect terminal to terminal');
        return;
      }

      const path = [this.selectedEndpoint.point, end.point];
      const lengthFt = this.measurePath(path);
      this.polylines.push({ points: path, lengthFt, type: 'conduit' });
      this.totalConduitFeet = this.polylines.filter(p => p.type === 'conduit').reduce((sum, p) => sum + p.lengthFt, 0);
      this.selectedEndpoint = null;
      this.redrawOverlay();
      return;
    }
  }

  onMouseMove(event: MouseEvent) {
    if (this.mode === 'pan') {
      if (!this.isPanning) return;
      const dx = event.clientX - this.lastMouse.x;
      const dy = event.clientY - this.lastMouse.y;
      this.panOffset.x += dx;
      this.panOffset.y += dy;
      this.lastMouse = { x: event.clientX, y: event.clientY };
      return;
    }

    if (this.mode === 'fiber' && this.drawingPath.length > 0 && this.overlayRef) {
      const rect = this.overlayRef.nativeElement.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
      this.hoverPoint = { x: x / this.zoom, y: y / this.zoom };
      this.redrawOverlay();
    }
  }

  onMouseUp() {
    if (this.mode === 'pan') {
      this.isPanning = false;
      return;
    }

    if (this.mode === 'fiber' || this.mode === 'conduit') {
      // Mouse up ends panning only; path commit happens on double-click (fiber) or second click (conduit)
      return;
    }
  }

  getTransform(): string {
    return `translate(${this.panOffset.x}px, ${this.panOffset.y}px)`;
  }

  zoomIn() {
    this.zoom = Math.min(3, this.zoom * 1.2);
    this.renderPage();
  }

  zoomOut() {
    this.zoom = Math.max(0.5, this.zoom / 1.2);
    this.renderPage();
  }

  setMode(next: 'pan' | 'calibrate' | 'fiber' | 'conduit' | 'terminal' | 'drop') {
    this.mode = next;
    this.isPanning = false;
    if (next !== 'fiber' && next !== 'conduit') {
      this.drawingPath = [];
      this.hoverPoint = null;
    }
    if (next !== 'conduit') {
      this.selectedEndpoint = null;
    }
    if (next !== 'calibrate') {
      this.calibrationPoints = [];
    }
    this.redrawOverlay();
  }

  clearOverlay() {
    this.polylines = [];
    this.drawingPath = [];
    this.hoverPoint = null;
    this.selectedEndpoint = null;
    this.toastMessage = null;
    this.totalLengthFeet = 0;
    this.totalConduitFeet = 0;
    this.calibrationPoints = [];
    this.terminals = [];
    this.drops = [];
    this.redrawOverlay();
  }

  private finishCalibration() {
    if (this.calibrationPoints.length !== 2) return;
    const [p1, p2] = this.calibrationPoints;
    const dx = p2.x - p1.x;
    const dy = p2.y - p1.y;
    const distPx = Math.sqrt(dx * dx + dy * dy);
    const input = prompt('Enter real-world distance between points (feet):');
    const feet = input ? parseFloat(input) : NaN;
    if (!isNaN(feet) && distPx > 0) {
      this.scaleFeetPerPixel = feet / distPx;
    } else {
      this.scaleFeetPerPixel = null;
    }
    this.calibrationPoints = [];
  }

  private measurePath(path: { x: number; y: number }[]): number {
    let px = 0;
    for (let i = 1; i < path.length; i++) {
      const dx = path[i].x - path[i - 1].x;
      const dy = path[i].y - path[i - 1].y;
      px += Math.sqrt(dx * dx + dy * dy);
    }
    if (this.scaleFeetPerPixel) {
      return px * this.scaleFeetPerPixel;
    }
    return 0;
  }

  private commitDrawingPath() {
    if ((this.mode === 'fiber' || this.mode === 'conduit') && this.drawingPath.length > 1) {
      const lengthFt = this.measurePath(this.drawingPath);
      this.polylines.push({ points: [...this.drawingPath], lengthFt, type: this.mode });
      this.totalLengthFeet = this.polylines.filter(p => p.type === 'fiber').reduce((sum, p) => sum + p.lengthFt, 0);
      this.totalConduitFeet = this.polylines.filter(p => p.type === 'conduit').reduce((sum, p) => sum + p.lengthFt, 0);
    }
    this.drawingPath = [];
    this.hoverPoint = null;
    this.redrawOverlay();
  }

  private redrawOverlay() {
    if (!this.overlayCtx || !this.overlayRef) return;
    const ctx = this.overlayCtx;
    const overlay = this.overlayRef.nativeElement;
    ctx.clearRect(0, 0, overlay.width, overlay.height);

    // calibration points
    ctx.fillStyle = '#f97316';
    this.calibrationPoints.forEach(p => {
      ctx.beginPath();
      ctx.arc(p.x * this.zoom, p.y * this.zoom, 5, 0, Math.PI * 2);
      ctx.fill();
    });

    // polylines
    this.polylines.forEach(line => {
      if (line.points.length < 2) return;
      ctx.strokeStyle = line.type === 'fiber' ? '#2563eb' : '#9333ea';
      ctx.lineWidth = 2;
      ctx.beginPath();
      ctx.moveTo(line.points[0].x * this.zoom, line.points[0].y * this.zoom);
      for (let i = 1; i < line.points.length; i++) {
        ctx.lineTo(line.points[i].x * this.zoom, line.points[i].y * this.zoom);
      }
      ctx.stroke();
    });

    // current drawing
    if (this.drawingPath.length > 0) {
      ctx.strokeStyle = '#10b981';
      ctx.lineWidth = 2;
      ctx.setLineDash([6, 4]);
      ctx.beginPath();
      ctx.moveTo(this.drawingPath[0].x * this.zoom, this.drawingPath[0].y * this.zoom);
      for (let i = 1; i < this.drawingPath.length; i++) {
        ctx.lineTo(this.drawingPath[i].x * this.zoom, this.drawingPath[i].y * this.zoom);
      }

      // preview to hover point while placing vertices
      if (this.hoverPoint) {
        ctx.lineTo(this.hoverPoint.x * this.zoom, this.hoverPoint.y * this.zoom);
      }
      ctx.stroke();
      ctx.setLineDash([]);
    }

    // selected terminal highlight
    if (this.selectedEndpoint) {
      ctx.strokeStyle = '#22c55e';
      ctx.lineWidth = 3;
      ctx.beginPath();
      ctx.arc(this.selectedEndpoint.point.x * this.zoom, this.selectedEndpoint.point.y * this.zoom, 10, 0, Math.PI * 2);
      ctx.stroke();
    }

    // markers
    this.terminals.forEach(pt => this.drawMarker(pt, '#10b981'));
    this.drops.forEach(pt => this.drawMarker(pt, '#f59e0b'));
  }

  private findNearestEndpoint(target: { x: number; y: number }, requiredType?: 'drop'): { point: { x: number; y: number }; type: 'terminal' | 'drop' } | null {
    const hitRadius = 12 / this.zoom;
    const candidates: { point: { x: number; y: number }; type: 'terminal' | 'drop' }[] = [];
    if (!requiredType || requiredType === 'drop') {
      this.drops.forEach(p => candidates.push({ point: p, type: 'drop' }));
    }
    if (!requiredType) {
      this.terminals.forEach(p => candidates.push({ point: p, type: 'terminal' }));
    }
    let closest: { point: { x: number; y: number }; type: 'terminal' | 'drop' } | null = null;
    let best = Number.MAX_VALUE;
    candidates.forEach(c => {
      const dx = c.point.x - target.x;
      const dy = c.point.y - target.y;
      const dist = Math.sqrt(dx * dx + dy * dy);
      if (dist < best && dist <= hitRadius) {
        best = dist;
        closest = c;
      }
    });
    return closest;
  }

  private showToast(message: string) {
    this.toastMessage = message;
    if (this.toastTimer) {
      clearTimeout(this.toastTimer);
    }
    this.toastTimer = setTimeout(() => {
      this.toastMessage = null;
    }, 2200);
  }

  private drawMarker(pt: { x: number; y: number }, color: string) {
    if (!this.overlayCtx) return;
    this.overlayCtx.fillStyle = color;
    this.overlayCtx.strokeStyle = '#ffffff';
    this.overlayCtx.lineWidth = 2;
    this.overlayCtx.beginPath();
    this.overlayCtx.arc(pt.x * this.zoom, pt.y * this.zoom, 8, 0, Math.PI * 2);
    this.overlayCtx.fill();
    this.overlayCtx.stroke();
  }

  nextPage() {
    if (this.pdf && this.currentPage < this.pdf.numPages) {
      this.currentPage++;
      this.renderPage();
    }
  }

  prevPage() {
    if (this.pdf && this.currentPage > 1) {
      this.currentPage--;
      this.renderPage();
    }
  }
}
