import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';
import { ApiService } from '../../core/services/api.service';
import { Project } from '../../core/services/state.service';

@Component({
  selector: 'app-project-list',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  template: `
    <div class="space-y-6">
      <div>
        <h2 class="text-2xl font-bold mb-4">Projects</h2>
        <div class="bg-white p-6 rounded shadow space-y-4">
          <h3 class="text-lg font-semibold">Create New Project</h3>
          <input 
            #projectName
            type="text" 
            placeholder="Project name"
            class="w-full px-3 py-2 border rounded"
          >
          <input 
            #pdfFile
            type="file" 
            accept=".pdf"
            class="w-full px-3 py-2 border rounded"
          >
          <button 
            (click)="createProject(projectName.value, pdfFile.files?.[0])"
            class="w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Create Project
          </button>
        </div>
      </div>

      <div>
        <h3 class="text-lg font-semibold mb-4">Existing Projects</h3>
        <div *ngIf="projects.length === 0" class="text-gray-500">
          No projects yet. Create one above!
        </div>
        <div *ngIf="projects.length > 0" class="grid gap-4">
          <div *ngFor="let project of projects" class="bg-white p-4 rounded shadow">
            <h4 class="text-lg font-semibold">{{ project.name }}</h4>
            <p class="text-gray-600 text-sm mb-3">Total Length: {{ project.total_length_ft | number:'1.2-2' }} ft</p>
            <div class="flex gap-2">
              <a 
                [routerLink]="['/projects', project.id]" 
                class="flex-1 px-4 py-2 bg-blue-600 text-white rounded text-center hover:bg-blue-700"
              >
                Edit
              </a>
              <button 
                (click)="deleteProject(project.id)"
                class="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  `
})
export class ProjectListComponent implements OnInit {
  projects: Project[] = [];

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.loadProjects();
  }

  loadProjects() {
    this.apiService.getProjects().subscribe(
      (data: any) => {
        this.projects = data;
      },
      (error) => console.error('Failed to load projects', error)
    );
  }

  createProject(name: string, file: File | undefined) {
    if (!name || !file) {
      alert('Please enter a project name and select a PDF file');
      return;
    }
    this.apiService.createProject(name, file).subscribe(
      () => {
        this.loadProjects();
      },
      (error) => console.error('Failed to create project', error)
    );
  }

  deleteProject(id: number) {
    if (confirm('Are you sure you want to delete this project?')) {
      this.apiService.deleteProject(id).subscribe(
        () => {
          this.loadProjects();
        },
        (error) => console.error('Failed to delete project', error)
      );
    }
  }
}
